import React, { Component } from 'react';
import { onLogin, onUser, userAction } from '../../store/actions';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import './perfil.scss';
import PerfilHeader from './perfil-header';
import PerfilNav from './perfil-nav';

class Perfil extends Component {
  constructor(props) {
    super(props);
  }

  componentDidMount() {
    const { onLogin, user, onUser } = this.props;
    onLogin(true);
    const payload = {
      action: userAction.LOG_IN,
      data: user,
    }
    onUser(payload);
  }

  render() {
    const { user, userStore } = this.props;
    return(
      <div className="perfil_main_cont">
        <PerfilHeader user={user} />
        <PerfilNav user={user} id={user._id} posts={user.posts} />
      </div>
    );
  }
}

const mapStateToProps = state => ({ login: state.login, userStore: state.user });
const mapDispatchToProps = dispatch => bindActionCreators({ onLogin, onUser }, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(Perfil);
