import React, { Component } from 'react';
import fetch from 'isomorphic-unfetch';
import Link from 'next/link';
import Button from '../buttons/button';
import Logo from '../logo-2/logo';
import './header.scss';
import classnames from 'classnames';
import PopOver from '../btn-resp/popOver';
import Notifications from '../notifications/notifications';
import { connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { onNav } from '../../store/actions';
import { color } from '../btn-resp/css-layout';

const iconBars = (
  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 448 512">
    <style jsx>{`
      svg{
        fill: #fff;
        width: 20px;
        border: #fff;
        padding:3px;
        transition: ease .5s;
      }
      svg:hover{
        fill: ${color.danger};
        background: #fff;
      }
    `
    }
    </style>
    <path d="M16 132h416c8.837 0 16-7.163 16-16V76c0-8.837-7.163-16-16-16H16C7.163 60 0 67.163 0 76v40c0 8.837 7.163 16 16 16zm0 160h416c8.837 0 16-7.163 16-16v-40c0-8.837-7.163-16-16-16H16c-8.837 0-16 7.163-16 16v40c0 8.837 7.163 16 16 16zm0 160h416c8.837 0 16-7.163 16-16v-40c0-8.837-7.163-16-16-16H16c-8.837 0-16 7.163-16 16v40c0 8.837 7.163 16 16 16z" />
  </svg>
);


class Header extends Component{
  constructor(props){
    super(props);
    this.state = {
      down: false,
      notifications: null,
    }
  }

  componentDidMount() {
    const id = localStorage.getItem('id');
    if (id) {
      fetch(`http://localhost:4000/api/notifications/${id}`)
        .then(res => res.json())
        .then(res => this.setState({ notifications: res }));        
    }
    window.onscroll = () => {
      if(window.scrollY > 50) {
        this.setState({ down: true });
      } else {
        this.setState({ down: false });
      }
    }
  }

  render(){
    const { down, notifications } = this.state;
    const { isLogin } = this.props;
    return(
      <header className={classnames({
        scroll_header: down || isLogin,
        header_hero_main: true,
        shadow: down,
      })}>
        <div className="container-fluid header_main_cont animated fadeInDown">
          <Logo />
          <nav>
            {
              isLogin 
              ? (
                  <>
                    <Notifications notes={notifications}/>
                    <button style={{background: "transparent", border: "none"}} onClick={this.props.onNav} id="btn-resp" type="button">
                      {iconBars}
                      <style jsx>{`
                        button:focus{
                          outline: none;
                        }
                      `}
                      </style>
                    </button>
                    <PopOver popOpen={this.props.nav} />
                  </>
              )
              : <Link href="/login"><Button color="light">Login</Button></Link>
            }
          </nav>
        </div>
      </header>
    );
  }
};

const mapDispatchToProps = dispatch => bindActionCreators({ onNav }, dispatch);
const mapStateToProps = state => ({
  isLogin: state.user.isLogged,
  nav: state.nav,
});

export default Header;
