import React, { Component } from 'react';
import Layout from '../components/layout/layout';
import '../components/perfil2/base.scss';
import persons from '../test/persons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPiggyBank } from '@fortawesome/free-solid-svg-icons';

const user = persons[3];
const gift = {
  name: 'nike Air-band',
  price: 80,
  spot: 15,
  url:'/product/123456',
  img: '/static/shoe.jpg'
}

class Perfil extends Component{
  constructor(props){
    super(props);
  }

  render(){
    return(
      <Layout>
        <div className="perfil_hero_cont">
          <div className="container-fluid">
            <div className="row">
              <div className="perfil_info col-5">
                <div className="gift_cont">
                  <img src={gift.img} alt={gift.name}/>
                  <div className="gift_info">
                    <a href={gift.url}>{gift.name}</a>
                    <div className="spot_cont">
                      <button><FontAwesomeIcon icon={faPiggyBank} /></button>
                    </div>
                  </div>
                </div>
                <div className="user_info">
                  <h1 className="display-6">{user.name} {' '} {user.lastName}</h1>
                  <p>{user.frontPageQuote}</p>
                </div>
              </div>
              <div className="col-7">
                <img src={user.perfilImg} alt={user.name}/>
              </div>
            </div>
          </div>
        </div>
      </Layout>
    );
  }
};

export default Perfil;
