export const normalize = () => "margin: 0; padding: 0; box-sizing: border-box;";
export const color = {
  prim: "#669f09",
  sec: "#8bb940",
  tri: "#007f1a",
  danger: "#ff6c1a",
  alert: "#f9e453",
  gray1: "#ffffff80",
  gradient: (deg) => `linear-gradient(${deg}deg,#669f09, #8bb940, #007f1a)`,
};

export const font = {
  text: 'Open Sans',
  title: 'Sansita',
};

export const sizes = {
  xl: '72px',
  lg: '48px',
  mdLg: '36px',
  md: '24px',
  normal: '18px',
  normalMd: '16px',
  sm: '14px',
  xs: '11px',
};
