import React, { Component } from 'react';
import '../components/perfil/friends.scss';

const user = [
  {
    name: 'jhon',
    lastName: 'ebrio',
    perfilImg: '/static/friend-1.jpg',
    frontPageQuote: 'Debemos cuidar el mundo, yo siemre reciclo mis latas de cerveza'
  },
  {
    name: 'sacha',
    lastName: 'gray',
    perfilImg: '/static/friend-2.jpg',
    frontPageQuote: 'En el set de grabacion todos los condones se reciclan'
  },
  {
    name: 'rachel',
    lastName: 'star',
    perfilImg: '/static/friend-3.jpg',
    frontPageQuote: 'Debemos cuidar nuestras playas para evitar el sifilis'
  },
  {
    name: 'mia',
    lastName: 'kalifa',
    perfilImg: '/static/friend-4.jpg',
    frontPageQuote: 'No tengo nada que decir, solo entre aqui a subir mis videos para ganar greencoins'
  }
];

const Person = ({ perfilImg, name, lastName, frontPageQuote }) => (
  <div className="person_cont">
    <div className="person_header">
      <button title="Eliminar">X</button>
    </div>
    <div className="person_info">
      <img src={perfilImg} className="img-fluid" alt={name}/>
      <div className="person_description">
        <p>{name}{" "}{lastName}</p>
        <p>{frontPageQuote}</p>
      </div>
    </div>
    <div className="person_footer">
      <button>Agregar</button>
    </div>
  </div>
);

class Foo extends Component{
  constructor(props){
  super(props);
  this.state = {
    api: '',

  }
}

  render() {
    return(
      <section className="friends_main_cont">
        {
          user.map(user => (
            <Person 
            name={user.name}
            lastName={user.lastName}
            perfilImg={user.perfilImg}
            frontPageQuote={user.frontPageQuote}
          />
          ))
        }
      </section>
    );
  }
}

export default Foo;
